exports.sql_select = function (
  db,
  schema,
  column,
  where,
  where_data,
  return_data
) {
  var insert_column = "";
  column.forEach((i) => {
    insert_column += i + ", ";
  });

  var sqlQuery = `SELECT ${insert_column.slice(0, -2)} 
                  FROM ${schema} 
                  ${where}`;
  db.all(sqlQuery, where_data, (err, result) => {
    if (err) {
      return_data = 0;
    } else {
      if (result[0] === undefined) {
        console.log("check 1");
        return_data = 1;
      } else {
        console.log("check 2");
        return_data = 2;
      }
    }
  });
};
