import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import PostTd from "./PostTd";
import io from "socket.io-client";

interface PostItem {
  order: number;
  id: string;
  name: string;
  date: string;
  title: string;
  content: string;
}

interface PostTrProps {
  info: PostItem[];
  user_id: string;
  notice_complaint: string;
  cur_num: number;
  handleDataChange: () => void;
}

const PostBoard: React.FC<PostTrProps> = ({
  info,
  user_id,
  notice_complaint,
  cur_num,
  handleDataChange,
}) => {
  return (
    <div>
      <h1 className="postboard_main_h1">공지사항</h1>
      <table className="table">
        <thead className="thead">
          <tr className="tr1">
            <th className="contents1">순번</th>
            <th className="contents1">작성자</th>
            <th className="contents1">작성날짜</th>
            <th className="contents1">제목</th>
            <th className="contents1">편집</th>
            <th className="contents1">삭제</th>
          </tr>
        </thead>
        <PostBoard
          info={notice}
          user_id={""}
          notice_complaint={"로그인 공지사항"}
          cur_num={cur_num}
          handleDataChange={set_data_change}
        />
      </table>
      <table>
        <thead>
          <tr className="direction_button">
            <th className="left_button" onClick={go_left}>
              ◀
            </th>
            <th className="page_button">
              {Number((cur_num / 10).toFixed()) + 1} page
            </th>
            <th className="right_button" onClick={go_right}>
              ▶
            </th>
          </tr>
        </thead>
      </table>
    </div>
  );
};

export default PostBoard;
