import "../Style/Login.css";
import React, { useState } from "react";
import { Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import io from "socket.io-client";
const socket = io("http://localhost:3001", { transports: ["websocket"] });

function Login() {
  const [state, setState] = useState({
    id: "",
    pw: "",
  });
  const navigate = useNavigate();

  const navigateToFindid = () => {
    navigate("/find_id");
  };

  const navigateToFindpw = () => {
    navigate("/find_pw");
  };

  const navigateToCreate = () => {
    navigate("/create");
  };

  function Send_Login_ID() {
    return new Promise((resolve, reject) => {
      socket.emit("Send Login ID Check", {
        id: state.id,
      });
      setState({
        ...state,
        id: "",
      });
      resolve(0);
    });
  }

  function Rec_Login_ID() {
    return new Promise((resolve, reject) => {
      socket.on("Receive Login ID Check", (message) => {
        resolve(message.ID_check);
      });
    });
  }

  function Send_Login_PW() {
    return new Promise((resolve, reject) => {
      socket.on("Receive Login PW Check", (message) => {
        resolve(message.PW_check);
      });
    });
  }

  async function Login_check() {
    var ID_check = await Rec_Login_ID();
    if (ID_check === 2) {
      var PW_check = await Send_Login_PW();
    } else if (ID_check === 1) {
      alert("중복된 아이디입니다.");
    } else {
      alert("관리자에게 문의하세요.");
    }
  }

  return (
    <div className="right_box">
      <div className="login_form">
        <div className="login_top_section">
          <div className="login_input_section">
            <input
              type="text"
              name="id"
              placeholder="아이디"
              className="login_id"
            ></input>
            <input
              type="password"
              name="pass"
              placeholder="비밀번호"
              className="login_password"
            ></input>
          </div>
          <form method="get">
            <button type="button" className="login_btn" onClick={Login_check}>
              로그인
            </button>
          </form>
        </div>
        <div className="login_option">
          <form method="get" className="option1">
            <button
              type="submit"
              className="option1_inner"
              onClick={navigateToFindid}
            >
              아이디
            </button>
          </form>
          <form method="get" className="option2">
            <button
              type="submit"
              className="option2_inner"
              onClick={navigateToFindpw}
            >
              비밀번호
            </button>
          </form>
          <form method="get" className="option3">
            <Link to="/create">
              <button
                type="submit"
                className="option3_inner"
                // onClick={navigateToCreate}
              >
                회원가입
              </button>
            </Link>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Login;
