import React, { useState, useEffect } from "react";
import io from "socket.io-client";
const socket = io("http://localhost:3001");

// 테스트 페이지 나중에 login 페이지와 연결시 몇가지를 가져다 사용할 예정
function Login() {
  const [state, setState] = useState({
    id: "",
    pw: "",
    Duplicate: 0,
    cur_Duple: 1,
    pre_Duple: 0,
  });

  function handleChange(e: any) {
    setState({
      ...state,
      [e.target.name]: e.target.value,
    });
  }

  function send_msg() {
    socket.emit("Send Duplicate Check", {
      id: state.id,
      pre_Duple: state.pre_Duple,
      cur_Duple: state.cur_Duple,
    });
    setState({
      ...state,
      id: "",
      pre_Duple: state.pre_Duple,
      cur_Duple: state.cur_Duple,
    });
  }

  function rec_msg() {
    socket.on("receive message", (message) => {
      setState({
        ...state,
        pre_Duple: message.pre_Duple,
      });
    });
  }

  function pre_id_Check() {
    if (state.id === "") {
      alert("아이디를 입력해주세요.");
    } else {
      send_msg();
      rec_msg();

      while (true) {
        if (state.pre_Duple === state.cur_Duple) {
          setState({
            ...state,
            cur_Duple: state.cur_Duple + 1,
          });
          if (state.Duplicate === 0) {
            alert("0 입니다.");
          } else if (state.Duplicate === 1) {
            alert("1 입니다.");
          } else {
            alert("2 입니다.");
          }
          break;
        }
      }
    }
  }

  return (
    <div>
      <form>
        <input
          type="text"
          name="id"
          placeholder="id"
          value={state.id}
          onChange={handleChange}
        ></input>
        <input
          type="password"
          name="pw"
          placeholder="pw"
          value={state.pw}
          onChange={handleChange}
        ></input>
        <button type="submit" onClick={pre_id_Check}>
          전송
        </button>
      </form>
    </div>
  );
}
export default Login;
