import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import "../Style/PostBoard_Main.css";
import Login from "./Login";
import PostBoard from "./PostBoard";
import io from "socket.io-client";
import TabMenu from "./TabMenu";
import Test_Menu from "./Test_Menu";

const PostBoard_Data_Save = require("./Function/PostBoard_Data_Save.js");
const PostBoard_Max_Num = require("./Function/PostBoard_Max_Num.js");

interface PostItem {
  order: number;
  id: string;
  name: string;
  date: string;
  title: string;
  content: string;
}

const PostBoard_Main: React.FC = () => {
  const location = useLocation();
  const user_info = { ...location.state };
  const [notice, setNotice] = useState<PostItem[]>([]);
  const [cur_num, setCurNum] = useState<number>(user_info.cur_num);
  const [loading, setLoading] = useState(false);
  const [data_change, setDataChange] = useState<number>(1);
  const [page, setPage] = useState<string>("테스트");

  const set_data_change = () => {
    setDataChange(data_change * -1);
  };

  const test = () => {
    alert("1");
    if (page === "테스트") {
      <PostBoard
        info={notice}
        user_id={""}
        notice_complaint={"로그인 공지사항"}
        cur_num={cur_num}
        handleDataChange={set_data_change}
      />;
    }
  };

  return (
    <form className="postboard_main_main_form">
      <form className="postboard_main_left_form">
        <form className="postboard_main_left_top_form">
          <Test_Menu />
        </form>
        <form className="postboard_main_left_bottom_form">test();</form>
      </form>
      <form className="postboard_main_right_form">
        <Login />
      </form>
    </form>
  );
};

export default PostBoard_Main;
