import io from "socket.io-client";

exports.Send_Duplicate = function (socket, id) {
  return new Promise((resolve, reject) => {
    socket.emit("Send Duplicate Check", {
      id: id,
    });
    resolve(0);
  });
};

exports.Rec_Duplicate = function (socket) {
  return new Promise((resolve, reject) => {
    socket.on("Receive Duplicate Check", (message) => {
      resolve(message.Duplicate);
    });
  });
};

exports.Duplicate_check = async function (socket, id) {
  if (id === "") {
    alert("아이디를 입력해주세요.");
  } else {
    await this.Send_Duplicate(socket, id);
    var Duplicate = await this.Rec_Duplicate(socket);

    if (Duplicate === 2) {
      alert("아이디가 중복됩니다.");
    } else if (Duplicate === 1) {
      alert("사용가능한 아이디입니다.");
    } else {
      alert("관리지에게 문의하세요.");
    }
  }
};
